	ark.parseTweetContent = function(tweet)	{
		//Link
		tweet = tweet.replace(/(\b(https?|ftp|file):\/\/[-A-Z0-9+&@#\/%?=~_|!:,.;]*[-A-Z0-9+&@#\/%=~_|])/ig, function(url) {
		    return '<a href="'+url+'" target="_blank">'+url+'</a>';
		});
		
		//User name
		tweet = tweet.replace(/[@]+[A-Za-z0-9_]+/g,function(reply) {
		    return  reply.charAt(0)+'<a href="http://twitter.com/'+reply.substring(1)+'" target="_blank">'+reply.substring(1)+'</a>';
		});
		//Hashtag
		tweet = tweet.replace(/#([a-zA-Z0-9]+)/g, function(hash) {
		    return '<a class="hashtag" target="_blank" href="http://twitter.com/#search?q='+hash.substring(1)+'" target="_blank">#'+hash.substring(1)+'</a>';
		});
		
		return tweet;
	};
	
	
	
	
