/* !-- Constructor -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	function ParallaxBackground(parallaxElement, options) {
		// Private Variables
		var _parallaxElement = parallaxElement;
		var _options = options ? options : {};
		
		// Getters and Setters
		this.parallaxElement = function() { return _parallaxElement; };
		this.options = function() { return _options; };
		this.scrollRatio = function() { return getScrollRatio(this); };
		
		
		// Binds the ParallaxBackground object to the HTML element
		if (!_parallaxElement.ark) _parallaxElement.ark = {};
		_parallaxElement.ark.parallaxBackground = this;
		
		this.setBackgroundPosition();
	}




