	/* -- Returns the scroll ratio of the element -- */
	/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	function getScrollRatio(context) {
		var elementViewportOffset = context.parallaxElement().getBoundingClientRect();
		//var ratio = Math.abs(1-(elementViewportOffset.top + elementViewportOffset.height) / (window.innerHeight + elementViewportOffset.height));
		var ratio = (elementViewportOffset.top + elementViewportOffset.height) / (window.innerHeight + elementViewportOffset.height);
		
		if ( isNaN(ratio) ) ratio = 0;
		if ( ratio < 0 ) ratio = 0;
		if ( ratio > 100 ) ratio = 1;
		
		return ratio
	}




