function ShowHideListItems_PageLoad() {
	var accordions = document.querySelectorAll("dl.showHideListItems"),
		options,
		dataOptions;
	
	for ( var i = 0; i < accordions.length; i++ ) {
		options = {};
		dataOptions = accordions[i].attributes.getNamedItem("data-showhidelistitems-options");
		
		if (dataOptions)
			options = eval("(" + dataOptions.value + ")");
		
		accordions[i].showHideListItems = new ShowHideListItems(accordions[i], options);
	}
}



function ShowHideListItems_PageResize() {
	setTimeout(function() {
		var accordions = document.querySelectorAll("dl.showHideListItems"),
			dtItems = null;
		
		for ( var i = 0; i < accordions.length; i++ ) {
			dtItems = accordions[i].querySelectorAll("dt");
			for (var j = 0; j < dtItems.length; j++) {
				if (dtItems[j].showned === true) {
					ShowHideListItems_SetItemHeight(dtItems[j].refDD, null);
				}
			}
		}
	}, 100);
}


function ShowHideListItems_SetItemHeight(item, forcedHeight) {
	var wrap = item.querySelector(".wrapper");
	if (wrap!== null) {
		item.style.height = (forcedHeight !== null ? forcedHeight : wrap.getBoundingClientRect().height) + "px";
	}
}


function ShowHideListItems(element, oOptions) {
	this.element = typeof(element) == "string" ? document.getElementById(element) : element;
	
	if ( this.element ) {
		if ( this.element.nodeName.toLowerCase() == "dl" ) {
			this.options = oOptions ? oOptions : new Object();
			this.init = ShowHideListItems_Init;
			this.show = ShowHideListItems_Show;

			this.init();
		}
	}
}

function ShowHideListItems_Init() {
	aDT = this.element.getElementsByTagName("dt");
	aDD = this.element.getElementsByTagName("dd");

	for ( var cDT = 0; cDT < aDT.length; cDT++ ) {
		var dt = aDT[cDT];
		dt.refObject = this;
		dt.refDD = aDD[cDT];

		dt.style.cursor = "pointer";
		dt.refDD.className = "hidden";

		dt.showned = false;
		dt.onclick = function () {
				if ( this.refObject.options.showHashItem )
					window.location.hash = this.id;
				this.refObject.show(this);
			};
	}

	//if ( this.options.showHashItem ) {
		var hashItem = new String();
		if ( hashItem = window.location.hash ) {
			while (hashItem.indexOf("#") == 0)
				hashItem = hashItem.substring(1);

			for ( var cDT = 0; cDT < aDT.length; cDT++ ) {
				if ( hashItem == aDT[cDT].id ) {
					aDT[cDT].onclick();
					cDT = aDT.length;
				}
			}
		}
	//}

	if (!isNaN(this.options.defaultItem) && !this.activeElement ) {
		if (aDT[this.options.defaultItem])
			aDT[this.options.defaultItem].onclick();
	}
}

function ShowHideListItems_Show(dt) {
	if ( this.options.showOne ) {
		if ( dt != this.activeElement && this.activeElement ) {
			this.activeElement.showned = !this.activeElement.showned;
			this.activeElement.className = this.activeElement.showned ? "opened " + this.activeElement.className : this.activeElement.className.replace(/opened\s/gi, "");
			ShowHideListItems_SetItemHeight(this.activeElement.refDD, this.activeElement.showned ? null : 0);
			this.activeElement.refDD.className = this.activeElement.showned ? "opened" : "closed";
		}

		this.activeElement = dt;
		dt.showned = !dt.showned;
		dt.className = dt.showned ? "opened " + dt.className : dt.className.replace(/opened\s/gi, "");
		ShowHideListItems_SetItemHeight(dt.refDD, dt.showned ? null : 0);
		dt.refDD.className = dt.showned ? "opened" : "closed";
		this.activeElement = dt.showned ? dt : null;
	} else {
		
		dt.showned = !dt.showned;
		dt.className = dt.showned ? "opened " + dt.className : dt.className.replace(/opened\s/gi, "");
		ShowHideListItems_SetItemHeight(dt.refDD, dt.showned ? null : 0);
		dt.refDD.className = dt.showned ? "opened" : "closed";
		this.activeElement = dt;
	}
}

