/* !-- Sets the page header -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.setPageHeader = function() {
		var pageHeader = document.getElementById("page-header");
		
		if (pageHeader) {
			var slides = pageHeader.querySelector(".slides");
			
			if (slides) {
				var allSlides = slides.querySelectorAll(".slide"),
					slideLinks = null;
				
				if(allSlides.length > 0) {
					// Removes the videos from the content on mobile devices and replaces it with an image
					if ( ark.isMobile() ) {
						var mobileVideos = slides.querySelectorAll(".background-image video"),
							imageNode = null;
						for ( var i = 0; i < mobileVideos.length; i++ ) {
							imageNode = mobileVideos[i].getElementsByTagName("img")[0];
							
							// We remove the current image node from the video tag
							if ( imageNode ) {
								imageNode.parentNode.removeChild(imageNode);
							}
							
							// No image was found as an alternate content. We try to create one with the video tag poster attribute.
							else if (!imageNode && mobileVideos[i].attributes.getNamedItem("poster")) {
								imageNode = document.createElement("img");
								imageNode.setAttribute("src", mobileVideos[i].attributes.getNamedItem("poster").value);
								imageNode.setAttribute("alt", "");
							}
							
							// If we found an image replacement, we add it to the content
							if ( imageNode )
								mobileVideos[i].parentNode.appendChild(imageNode);
							
							// Removes the video
							mobileVideos[i].parentNode.removeChild(mobileVideos[i]);
						}
					}
					
					
					// Adds the behavior on the page header aside video links
					function addAsideVideoLinkBehavior() {
						var asideVideoLinks = slides.querySelectorAll(".aside a.video-link");
						
						function asideVideoLinksClick(evt) {
							var videoLink = this.getAttribute("href"),
								videoType = null;
							
							if ( (/\byoutube\b/).test(videoLink) ) {
								videoType = "YOUTUBE";
							}
							
							else if ( (/\btwitch\b/).test(videoLink) ) {
								videoType = "TWITCH";
							}
							
							
							function addLinkProperty(property, value) {
								if ( !(new RegExp(property + "=")).test(videoLink) ) {
									videoLink += ((/\?/).test(videoLink) ? "&" : "?") + property + "=" + value;
								}
							}
							
							addLinkProperty("rel", "0");
							addLinkProperty("autoplay", "1");
							
							if ( videoType === "YOUTUBE" ) {
								jQuery.fancybox.open({href:videoLink, type: "iframe", width: 640, height: 360, aspectRatio: true });
							} else {
								window.open(videoLink);
							}
							
							if ( !evt ) evt = window.event;
							if (evt.preventDefault) evt.preventDefault();
							evt.returnValue = false;
							return false;
						}
						
						
						for ( var i = 0; i < asideVideoLinks.length; i++ ) {
							ark.Events.add(asideVideoLinks[i], "click", asideVideoLinksClick);
						}
					}
					
					
					// Initialization
					function initPageHeaderVideo() {
						function videoEndedEvent(evt) {
							var repeatTimes = parseFloat(this.getAttribute("data-video-repeat-times"));
							
							if (isNaN(repeatTimes)) repeatTimes = 1;
							if (this.playCount === undefined) this.playCount = 0;
							
							this.playCount++;
							
							if ( this.playCount >= repeatTimes && allSlides.length > 1) {
								this.pause();
								this.playCount = 0;							
								setTimeout(function(){
									jQuery(slides).trigger("play.owl.autoplay");
								}, 1);
							} else {
								ark.Events.remove(this, "ended", videoEndedEvent);
								
								var loopTime = parseFloat(this.getAttribute("data-video-loop-time"));
								if (isNaN(loopTime)) loopTime = 0;

								this.currentTime = loopTime;
								
								this.play();
								var _this = this;
								setTimeout(function() {
									ark.Events.add(_this, "ended", videoEndedEvent);
									_this.play();
								}, 100);
							}
						}
						
						
						var videos = slides.getElementsByTagName("video");
						
						for ( var i = 0; i < videos.length; i++ ) {
							videos[i].autoplay = false;
							ark.Events.add(videos[i], "ended", videoEndedEvent);
						}
						
						addAsideVideoLinkBehavior();
						
						playPageHeaderVideo();
					}
					
					
					// Video player function
					function playPageHeaderVideo() {
						var slideItems = slides.querySelectorAll(".owl-item");
							activeSlides = slides.querySelectorAll(".owl-item.active"),
							videoNode = null;
						
						// Stops all videos
						for ( var i = 0; i < slideItems.length; i++ ) {
							videoNode = slideItems[i].querySelector(".background-image video");
							if (videoNode) {
								videoNode.pause();
								try {
									videoNode.currentTime = 0;
								} catch(e) {};
								videoNode.playCount = 0;
							}
						}
						
						// Plays the video for the active slides
						setTimeout(function(){
							for ( var i = 0; i < activeSlides.length; i++ ) {
								videoNode = activeSlides[i].querySelector(".background-image video");
								if (videoNode) {
									jQuery(slides).trigger("stop.owl.autoplay");
									videoNode.play();
								}
							}
						}, 1);
					}
					
					
					// Adds tracking for each links in the current slide
					for ( var i = 0; i < allSlides.length; i++ ) {
						slideLinks = allSlides[i].getElementsByTagName("a");
						for ( var j = 0; j < slideLinks.length; j++ ) {
							slideLinks[j].onclick = function() {
								try {
									_gaq.push(['_trackEvent', 'Heroes-Banner-homepage', 'Click', this.href]);
								} catch(e){
									console.error(e, ['_trackEvent', 'Heroes-Banner-homepage', 'Click', this.href]);
								}
								return;
							};
						}
					}
					
					// Creates a carousel if more than one item is in the page header
					if ( allSlides.length > 1 ) {
												
						
						jQuery(slides).owlCarousel({
							animateOut         : "fadeOut",
							animateIn          : "fadeIn",
							autoplay           : true,
							autoplayHoverPause : false,
							autoplayTimeout    : 10*1000,
							controlsClass      : "controls",
							dotClass           : "page",
							dots               : true,
							dotsClass          : "pager",
							items              : 1,
							loop               : true,
							mouseDrag          : false,
							nav                : true,
							navClass           : ["previous", "next"],
							navContainerClass  : "nav",
							navText            : ["&lt;","&gt;"],
							onTranslated       : playPageHeaderVideo,
							touchDrag          : true,
							onInitialized		: function() {
								ark.setHeaderCountdown();
								initPageHeaderVideo();
							}
						});
					}
					else {
						var videoNode = allSlides[0].querySelector("video");
						if(videoNode) {
							if(parseInt(videoNode.getAttribute("data-videoLoopTime")) > 0) {
								initPageHeaderVideo();
								playPageHeaderVideo();
							}
							else {
								videoNode.loop = true;
								videoNode.play();
							}
						}
						
						addAsideVideoLinkBehavior();
						ark.setHeaderCountdown();
					}
					
					// Places the header background image
					ark.placeHeaderBackground();
				}
			}
		}
	};




/* !-- Places the header background image -- */
