				nextTop = document.documentElement.getBoundingClientRect().top * speed;
				maxTop = slidesImages[i].parentNode.getBoundingClientRect().height - slidesImages[i].getBoundingClientRect().height;
				
				if (nextTop < maxTop) nextTop = maxTop;
