	ark.countDown = function(countdownElement) {
		var interval;
		interval = setInterval(function(){
			countdownProcessor(interval, countdownElement);
		}, 1000);
		countdownProcessor(interval, countdownElement);
		
		function countdownProcessor(interval, countdown) {
			var days = countdown.querySelector("span.days");
			var hours = countdown.querySelector("span.hours");
			var minutes = countdown.querySelector("span.minutes");
			var seconds = countdown.querySelector("span.seconds");
	
			var target = parseInt(countdown.getAttribute("data-target"));
			var now = Math.floor(new Date().getTime() / 1000);
			
			if((target - now) > 0) {
				var daysVal = Math.floor((target - now) / (3600 * 24));
				var hoursVal = Math.floor(((target - now) - (daysVal * 3600 * 24)) / 3600);
				var minutesVal = Math.floor(((target - now) - (daysVal * 3600 * 24) - (hoursVal * 3600)) / 60);
				var secondsVal = Math.floor(((target - now) - (daysVal * 3600 * 24) - (hoursVal * 3600) - (minutesVal * 60)));
				
				days.innerHTML = daysVal;
				hours.innerHTML = hoursVal;
				minutes.innerHTML = minutesVal;
				seconds.innerHTML = secondsVal;
				
	
			}
			else {
				days.innerHTML = "0";
				hours.innerHTML = "0";
				minutes.innerHTML = "0";
				seconds.innerHTML = "0";

				jQuery.ajax("/rotator_block/countdown_cta/" + countdown.dataset.bid + "/" + countdown.dataset.slideId).done(function(data){
					var json = JSON.parse(data);
					if(json) {
						if(json.url != "" && json.title != "") {
							
							//Create <a> if not exist
							jQuery('.countdown').each(function() {
								
								var btnLink = jQuery('a', this);
								
								if(jQuery(this).data('slide-id') == countdown.dataset.slideId) {
									if(btnLink.length) {
										jQuery(btnLink).attr("href", json.url).text(json.title);
									} else {
										jQuery(this).append('<a class="cta learn-more" onclick="_gaq.push([\'_trackEvent\', \'Homepage\', \'Click\', \'Countdown-CTA-' +json.title.replace(" ", "_")+ '-en\']);" href="'+ json.url +'">'+ json.title +'</a>');
									}
								}
							});
						}
					}
					else {
						//Remove All link
						jQuery('.countdown a').remove();
					}
					
					clearInterval(interval);
				});
			}
		}
		
		
		jQuery(countdownElement).fadeTo(400, 1);
	};



