/* !-- Managing Grid last slide  -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.gridEndSlide = function() {
		var items     = jQuery(".content-grid .item").length;
		var itemWidth = jQuery(".content-grid .item").width();
		var gridWidth = jQuery(".content-grid").width();
		var modulator = Math.round(gridWidth / itemWidth);
		var ratio     = (modulator - (items % modulator)) / modulator;
		
		if (ratio < 1)
			jQuery(".content-grid #end-slide").css("width", String(ratio*100) + "%").show();
		else
			jQuery(".content-grid #end-slide").hide();
		
	};




/* !-- Managing Articles Grid last slide  -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.gridArticlesEndSlide = function() {
		
		var cardOfTheDay = 0;
		
		if(jQuery("#card-day").length)
			cardOfTheDay = 1;
		
		if ( jQuery(".articles-bloc").length ) {
			var items     = jQuery(".articles-bloc .article").length + cardOfTheDay;
			var itemWidth = jQuery(".articles-bloc .article").width();
			var gridWidth = jQuery(".articles-bloc").width();
			var modulator = Math.round(gridWidth / itemWidth);
			var ratio     = (modulator - (items % modulator)) / modulator;
			
			
			if(ark.isMobile()) {
				jQuery("#last-slide").hide();
			} else {
				if (ratio < 1)
					jQuery(".articles-bloc #last-slide").css("width", String(ratio*100) + "%").show();
				else
					jQuery(".articles-bloc #last-slide").hide();
			}
			
		}
	};




/* !-- Places the article aside -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.placeArticleAside = function() {
		var aside = document.querySelector("#page.article_detail #content aside");
		var article = document.querySelector("#page.article_detail article");
		
		if (aside && article) {
			var asideContent = aside.querySelector(".aside-wrapper");
			
			if (asideContent) {
				var top = 0;
				
				if ( window.innerHeight - aside.getBoundingClientRect().bottom > 0) {
					top = window.innerHeight - aside.getBoundingClientRect().bottom;
					
					if ( asideContent.getBoundingClientRect().height + top >= article.getBoundingClientRect().height ) {
						top = article.getBoundingClientRect().height - asideContent.getBoundingClientRect().height;
					}
				} else {
					top = 0;
				}
				
				asideContent.style.webkitTransform = "translateY(" + top + "px)";
				asideContent.style.mozTransform = "translateY(" + top + "px)";
				asideContent.style.transform = "translateY(" + top + "px)";
			}
		}
	};




