/* !-- Handling anchors when there's a static menu -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.scrollToContent = function() {
		
		var anchor 			= window.location.hash;
		
		if(anchor && anchor != "#section-nav") {
			var mainMenu 		= jQuery("header").height();
			var spaceToScroll 	= jQuery(".subsection") ? (jQuery(".subsection").height() + mainMenu) : mainMenu;
			
			jQuery('html, body').scrollTop(jQuery(anchor).offset().top - spaceToScroll);
		}
	};
	
	
	
	
/* !-- Video Article Add -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.videoArticleAdd = function() {
		
		jQuery(".aside-wrapper .article.video a, .legacy_content .wrapper-movies a").each(function( e ) {
	
			var videoUrl	 	= jQuery(this).data("video-url");
			if(typeof videoUrl !== 'undefined') {
				var fancyOptions 	= {type: "iframe", width: 640, height: 360, aspectRatio: true, autoPlay: true }
				
				if ((/\byou(\.?)tu(\.?)be\b/).test(videoUrl)) {
					fancyOptions.href = videoUrl
				} else {
					
					var videoPath =  videoUrl;
					
					fancyOptions.content = '<video width="640" height="360" controls="controls">' +
						'<source type="video/mp4" src="'+ videoPath +'" />' +
						'<source type="video/webm" src="'+ videoPath +'" />' +
					'</video>';
				}
				
				jQuery(this).click(function(e){
					jQuery.fancybox.open(fancyOptions);
					e.preventDefault();
				});
			}
		});
	};
	




/* -- Sets the article reading progress -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.setArticleReadingProgress = function() {
		var articlePage = document.querySelector("#page.section_articles.article_detail");
		
		if (articlePage) {
			var headers = articlePage.querySelectorAll("#mobile-menu .header, header"),
				progressbar, bar;
			
			for ( var i = 0; i < headers.length; i++ ) {
				progressbar = document.createElement("div");
				progressbar.setAttribute("class", "reading-progress");
				
				bar = document.createElement("div");
				bar.setAttribute("class", "reading-progress-bar");
				
				progressbar.appendChild(bar);
				headers[i].appendChild(progressbar);
			}
			
			function setProgress() {
				var progressBars = document.querySelectorAll(".reading-progress .reading-progress-bar");
				var ratio = window.pageYOffset / (document.documentElement.getElementsByTagName("body")[0].offsetHeight - window.innerHeight);
				if (ratio < 0) ratio = 0;
				if (ratio > 1) ratio = 1;
				
				for ( var i = 0; i < progressBars.length; i++ ) {
					progressBars[i].style.width = String(ratio*100) + "%";
				}
			}
			
			setProgress();
			
			ark.Events.add(window, "scroll", setProgress);
			ark.Events.add(window, "resize", setProgress);
		}
	};




/* !-- Handles the cookie warning window -- */
/*
	Will display an HTML element to the user for him to be informed about the cookies based on his current locale
*/
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.handleCookieWarning = function() {
		var cookieWarningWindow = document.getElementById("cookies-warning");
		
		if ( cookieWarningWindow ) {
			var localStoragePropName = "cookieWarning",
				displayWarning = false,
				currentLocale = document.documentElement.attributes.getNamedItem("lang") ? document.documentElement.attributes.getNamedItem("lang").value : "en-US",
				cookieWarningSeen = localStorage.getItem(localStoragePropName) ? JSON.parse(localStorage.getItem(localStoragePropName)) : {};
			
			// Determines if the warning should be displayed
			if (!cookieWarningSeen[currentLocale]) {
				cookieWarningSeen[currentLocale] = { accepted: true, expires: expiresAt };
				displayWarning = true;
			} else {
				var today = new Date();
				var expiresAt = (new Date()).setFullYear(today.getFullYear() + 1);
				
				if ( cookieWarningSeen[currentLocale].expires < today ) {
					displayWarning = true;
				}
				
				cookieWarningSeen[currentLocale].expires = expiresAt;
			}
			
			
			// Displays the warning if necessary
			if (displayWarning) {
				
				var agreeButton = cookieWarningWindow.querySelector(".agree"),
					disagreeButton = cookieWarningWindow.querySelector(".disagree");
				
				// Closes the cookie warning window
				function closeCookieWarningWindow() {
					cookieWarningWindow.parentNode.removeChild(cookieWarningWindow);
				}
				
				// Adds the click event on the "agree" button
				if ( agreeButton ) {
					ark.Events.add(agreeButton, "click", function() {
						// Closes the cookie warning window
						closeCookieWarningWindow();
						
						// Writes the cookie warning into local storage
						localStorage.setItem(localStoragePropName, JSON.stringify(cookieWarningSeen));
					});
				}
				
				cookieWarningWindow.style.display = "block";
			}
		}
	};




/* !-- Smooth Scrolling to top -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	jQuery(function() {
		jQuery('a[href=#viewport]').click(function() {
			if (location.pathname.replace(/^\//,'') == this.pathname.replace(/^\//,'') && location.hostname == this.hostname) {
				var target = jQuery(this.hash);
				target = target.length ? target : jQuery('[name=' + this.hash.slice(1) +']');
				if (target.length) {
					jQuery('html,body').animate({
							scrollTop: 0
						}, 500);
					return false;
				}
			}
		});
	});
	
	
	// Back to top appear on scroll
	jQuery(document).scroll(function () {
		var y = jQuery(this).scrollTop();
		if (y > 300) {
			jQuery('#back-to-top').fadeIn();
		} else {
			jQuery('#back-to-top').fadeOut();
		}
	});




