/* !-- Article Tracking -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	(function(window){
		
		/* -- Global Variables -- */
		/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
		var pageReadTimeout       = 3*60*1000,
			articleReadCompletely = false;
		
		
		
		
		/* -- Tracks if an article has been 'read' completely (article element has been scrolled completely into view) -- */
		/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
		function detectArticleReadCompletely() {
			if (articleReadCompletely === true)
				return;
			
			if ( document.getElementById("page") && ( (/\barticle_detail\b/).test(document.getElementById("page").className) ) ) {
				var articleElement = document.getElementsByTagName("article")[0];
				
				// Adds the tracking only if it's an article
				if (articleElement) {
					var scrollPos      = window.pageYOffset,
						viewportHeight = document.documentElement.clientHeight,
						contentHeight  = articleElement.offsetTop + articleElement.offsetHeight;
					
					if ( scrollPos + viewportHeight >= contentHeight ) {
						articleReadCompletely = true;
						try {
							_gaq.push(['_trackEvent', 'Article', 'Engagement', 'scroll-all-page']);
						} catch(e) {
							console.error(e, ['_trackEvent', 'Article', 'Engagement', 'scroll-all-page']);
						}
					}
				}
			}
		}
		
		
		
		
		/* -- Starts the pageRead timeout counter -- */
		/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
		function addPageReadTimeout() {
			if ( document.getElementById("page") && ( (/\barticle_detail\b/).test(document.getElementById("page").className) ) ) {
				var articleElement = document.getElementsByTagName("article")[0];
				
				// Adds the tracking only if it's an article
				if (articleElement) {
					setTimeout(function() {
						try {
							_gaq.push(['_trackEvent', 'Article', 'Engagement', '3min-article']);
						} catch(e) {
							console.error(e, ['_trackEvent', 'Article', 'Engagement', '3min-article']);
						}
					}, pageReadTimeout);
				}
			}
		}
		
		// Attaches the events
		ark.Events.add(window, "load", detectArticleReadCompletely);
		ark.Events.add(window, "scroll", detectArticleReadCompletely);
		ark.Events.add(window, "resize", detectArticleReadCompletely);
		
		ark.Events.add(window, "load", addPageReadTimeout);
		
	}(window));




/* !-- Tracks page links -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
	ark.setLinkTracking = function() {
		var anchors    = document.getElementsByTagName("a"),
			domain     = document.domain.replace(/^www\./i, ""),
			link, linkDomain, extension, trackExtension, doContinue;
		
		for (var i = 0; i < anchors.length; i++ ) {
			doContinue = true,
			link = anchors[i].href;
			
			// Checks different outside links
			if ( (/^http(s)?:\/\//i).test(link) ) {
				linkDomain = link.replace(/^http(s)?:\/\/(www\.)?/i, "");
				linkDomain = linkDomain.substring(0, linkDomain.indexOf("/"));
				
				if (linkDomain != domain) {
					ark.Events.add(anchors[i], "click", function() {
						try {
							_gaq.push(['_trackEvent', 'Outbound links', 'Click', this.href]);
						} catch(e) {
							console.error(e, ['_trackEvent', 'Outbound links', 'Click', this.href]);
						}
					});
					doContinue = false;
				}
			}
			
			// Checks for file types for same domain links
			if (doContinue && !(/\/$/).test(link)) {
				extension = link.substring(link.lastIndexOf("/") + 1);
				extension = extension.substring(extension.lastIndexOf(".")).toUpperCase();
				
				if ( (/^\./).test(extension) ) {
					trackExtension = null;
					switch(extension) {
						case ".JPG":
						case ".PNG":
						case ".XML":
						case ".TXT":
						case ".JPEF":
						case ".CSV":
						case ".EXE":
						case ".ZIP":
						case ".PDF":
							anchors[i].trackingExtension = extension.replace(/^\./, "");
							break;
						default:
							break;
					}
					
					if (anchors[i].trackingExtension) {
						ark.Events.add(anchors[i], "click", function() {
							try {
								_gaq.push(['_trackEvent', 'Downloads', this.trackingExtension, this.href]);
							} catch(e) {
								console.error(e, ['_trackEvent', 'Downloads', this.trackingExtension, this.href]);
							}
						});
						
						doContinue = false;
					}
				}
			}
			
			// Checks for mail links
			if ( doContinue &&  (/^mailto:/).test(link) ) {
				ark.Events.add(anchors[i], "click", function() {
					try {
						_gaq.push(['_trackEvent', 'Mails', 'Click', this.href]);
					} catch(e) {
						console.error(e, ['_trackEvent', 'Mails', 'Click', this.href]);
					}
				});
				
				doContinue = false;
			}
		}
	};



