/* !-- Scrollable Header/Content -- */
/* - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - - */
(function() {
	window.addEventListener("DOMContentLoaded", function() {
		var requiredMinWidth = 1025,
			header = document.querySelector(".scrollable-header_header"),
			content = document.querySelector(".scrollable-header_content");
		
		if (header && content) {
			function headerTransitionListener(remove) {
				var method = remove !== true ? "addEventListener" : "removeEventListener";
				header[method]("webkitTransitionEnd", transitionListener);
				header[method]("oTransitionEnd", transitionListener);
				header[method]("transitionend", transitionListener);
			}
			
			function transitionListener() {
				header.isAnimating = false;
				headerTransitionListener(true);
			}
			
			function scrollableHeaderScrollListener() {
				if (window.innerWidth >= requiredMinWidth) {
					if (header.isAnimating !== true) {
						if ( header.getBoundingClientRect().top + header.getBoundingClientRect().height <= 0 ) {
							if ( !header.classList.contains("scrolled") ) {
								header.isAnimating = true;
								header.classList.add("scrolled");
								content.classList.add("scrolled");
								headerTransitionListener();
							}
						} else {
							header.classList.remove("scrolled");
							content.classList.remove("scrolled");
						}
					} else {
						window.scrollTo(0, header.getBoundingClientRect().height);
					}
				}
			}
			
			window.addEventListener("scroll", scrollableHeaderScrollListener, false);
		}
	}, false);
})();




