(function() {
	function initCardRevealCarousels() {
		var carousels = document.querySelectorAll(".card-reveal-carousel:not([data-card-reveal-carousel-initiated])"),
			cardItems,
			cardPosition,
			i, j;
		
		function flip() {
			var _cardItemReference = this.getAttribute("data-card-item-reference"),
				_clones = this.carousel.querySelectorAll(".card-reveal-carousel_item[data-card-item-reference='" + _cardItemReference + "']"),
				_classMethod = this.classList.contains("flipped") ? "remove" : "add",
				_i;
			
			for ( _i = 0; _i < _clones.length; _i++ ) {
				_clones[_i].classList[_classMethod]("flipped");
			}
		}
		
		function flipCardButtonClicked() {	
			this.cardItem.flip();
		}
		
		function carouselInitialized() {
			var _carousel = jQuery(this)[0].$element[0];
			
			var _cardItems = _carousel.querySelectorAll(".card-reveal-carousel_item"),
				_flipButton = null,
				_i = 0;
			
			for ( _i = 0; _i < _cardItems.length; _i++ ) {
				_flipButton = _cardItems[_i].querySelector(".card-reveal-carousel_flip-button");
				
				if (_flipButton) {
					_cardItems[_i].carousel = _carousel;
					_cardItems[_i].flip = flip;
					_flipButton.cardItem = _cardItems[_i];
					_flipButton.addEventListener("click", flipCardButtonClicked, false);
				}
			}
		}
		
		for ( i = 0; i < carousels.length; i++ ) {
			carousels[i].setAttribute("data-card-reveal-carousel-initiated", "true");
			cardItems = carousels[i].querySelectorAll(".card-reveal-carousel_item");
			
			// Adds a reference for each card
			for ( j = 0; j < cardItems.length; j++ ) {
				cardItems[j].setAttribute("data-card-item-reference", String(j+1));
				cardItems[j].querySelector(".position").innerHTML = String(j+1) + "/" + String(cardItems.length);
			}
			jQuery(cardItems).show();
			jQuery(carousels[i].querySelector(".card-reveal-carousel_carousel")).owlCarousel({
				nav                   : true,
				loop                  : true,
				center                : true,
				dots                  : false,
				mouseDrag             : false,
				navText               : false,
				navRewind             : false,
				onInitialized         : carouselInitialized,
				responsiveRefreshRate : 1,
				responsive            : {
					0    : { items: 1 },
					480  : { items: 2 },
					720  : { items: 3 },
					944  : { items: 4 },
					1150  : { items: 5 }
				}
			});			
		}

	}
	
	document.addEventListener("DOMContentLoaded", initCardRevealCarousels);
})();;
